INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (38,'Anacortes Brewing','320 Commercial Avenue',NULL,'Anacortes','Washington','98221','United States','360-588-1720','http://www.anacortesrockfish.com/',NULL,'Our brewers focus on high quality ingredients, a carefully controlled process and delicate handling to provide one of the best beers you will find in the industry. We operate a seven barrel brewery designed and installed by Sound Brewing Systems of Seattle with vessels fabricated by Ripley Stainless in British Columbia. Our brewery features a cold room specially designed for lagering to provide our beers with a traditional character and complexity.',0,'2010-07-22 20:00:20 UTC');
