INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (123,'Big Time Brewing','4133 University Way NE',NULL,'Seattle','Washington','98105','United States','1-206-545-4509','http://www.bigtimebrewery.com/',NULL,'Big Time''s brewery is a 14 barrel JV Northwest system consisting of a gas fired brew kettle, an infusion mash tun with mixer, a hot liquor back, a wort cooler, (5) 14 barrel fermenters, a diatomaceous earth filter, a bright beer tank, and (12) 7 bbl serving tanks.  We sell most of our beer at the pub but do wholesale a limited number of kegs to a few Seattle area taverns. We currently brew approximately 1,500 barrels of beer per year',0,'2010-07-22 20:00:20 UTC');
