INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (136,'Blacksburg Brewing Company','P.O. Box 315',NULL,'Blacksburg','Virginia','24063','United States',NULL,'http://www.blacksburgbrewing.com/',NULL,'Blacksburg Brewing Co. (BBC) is an independent specialty brewer of premium beer.  As mandated by the Reinheitsgebot (German Purity Law), our beer is brewed with only 4 ingredients: water, barley malt, hops, and yeast.  The best beer requires the best ingredients, so we purchase them regardless of the cost.  We pay a premium to import the Cadillac of barley malt from Bamberg, and our hops from The Hallertau are the best that money can buy, period.',0,'2010-07-22 20:00:20 UTC');
