INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (145,'Blue Ridge Brewing','217 North Main Street',NULL,'Greenville','South Carolina','29601','United States','1-864-232-4677','http://www.blueridgebrewing.com/',NULL,'Born in the foothills of the Blue Ridge Mountains, Greenville''s Brew-Pub continues to pay homage to one simple creed, to provide our customers the very best we can! We''re as proud of Chef Rich''s House-Made Menu as we are of Jay''s Freshly Crafted Brews!',0,'2010-07-22 20:00:20 UTC');
INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (146,'Bluegrass Brewing Company, Inc.','3929 Shelbyville Rd.',NULL,'Louisville','Kentucky','40207','United States','502-899-7070','http://www.bbcbrew.com',NULL,'The original Bluegrass Brewing Company opened in 1993 at this location, 3929 Shelbyville Road, near Westport Rd., & Chenoweth, & Breckenridge Lanes. It is the city''s oldest remaining brewpub and was the first to make a significant mark on the national brewpub and microbrewery scene. Louisville''s best beer is made at this location, and large glass windows in the back dining room allow you to witness the brewing operation. The headbrewer is Jerry Gnagy, assisted by Sam Cruz and Cody Mingus.',0,'2010-07-22 20:00:20 UTC');
INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (147,'Bobcat Cafe & Brewery','4 Main Street',NULL,'Bristol','Vermont','5443','United States','(802) 453-3311','http://www.bobcatcafe.com/',NULL,'Nestled at the foot of the Green Mountains & located right in the heart of Bristol, The Bobcat Cafe & Brewery is just walking distance from the town green, downtown shopping & the Crystal Palace B&B. Established in 2001, this popular, community oriented cafe & brewery features a wide selection of house brewed beers, a diverse menu to meet any taste, carefully selected wines & a full bar.',0,'2010-07-22 20:00:20 UTC');
