INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (374,'Chelsea Brewing Company','Chelsea Piers, Pier 59',NULL,'New York','New York','10011','United States','212.336.6440','http://chelseabrewingco.com/',NULL,'Chelsea Brewing Company, rated the best brewery in New York, is a first class waterfront restaurant and the only micro-brewery in Manhattan located at pier 59.With over 15 years of experience, and 8 National Awards, we are proud to serve up to 27 natural hand-crafted ales and specialty beers using only the finest malts and hops from around the world. We offer beer tastings, brewery tours and group packages.',0,'2010-07-22 20:00:20 UTC');
