INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (378,'Chimay (Abbaye Notre Dame de Scourmont)','Route de Charlemagne 8',NULL,'Chimay',NULL,NULL,'Belgium','+32 (0)60 21 03 27','http://www.chimay.com',NULL,'Chimay is an authentic Trappist beer that is it is brewed within a Trappist monastery, under the control and responsibility of the monastic community. Only 6 beers in Belgium can carry the appellation Trappist: Chimay, Orval, Rochefort, Westmalle, Westvleteren and Achel.',0,'2010-07-22 20:00:20 UTC');
