INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (404,'Coronado Brewing Company','170 Orange Avenue',NULL,'Coronado','California','92118','United States','1-619-437-4452','http://www.coronadobrewingcompany.com/',NULL,'The Coronado Brewing Company, in beautiful Coronado (located just a few minutes from downtown San Diego) welcomes you to come and enjoy quality handcrafted beers and delicious food in a casual family environment. Join us for a refreshing pint while we make you one of our signature pizzas or calzones in the wood fired oven, or choose from a wide selection of fresh seafood, steaks, pastas and dining specials. The Coronado Brewing Company is perfect for a quick lunch, a relaxing beverage or a wonderful dinner. If you''re a sports fan come relax with a cold beer and an appetizer while watching your favorite team on one of our many HD TV''s. Open 7 days a week from 11:00 am (until closing) & offering a weekly happy hour (M-F) from (2-5pm).',0,'2010-07-22 20:00:20 UTC');
