INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (410,'Crabtree Brewery','625 3rd St. #D',NULL,'Greeley','Colorado','80631','United States','[970] 356-0516','http://www.crabtreebrewing.com',NULL,'The Crabtree Brewing Company is dedicated to serving the environment, the community, our customers, and our valued employees.  Requiring the best ingredients to support unique premium beers.  Keeping a dynamic business mind while remaining modestly profitable.   Employing technology, embracing ethics, growing to meet the needs of our customers and employees, and having fun doing what we loveï¿½.Making Great Beer.',0,'2010-07-22 20:00:20 UTC');
