INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (546,'Foster''s Australia Ltd.',NULL,NULL,NULL,NULL,NULL,'Australia',NULL,'http://www.fostersbeer.com/',NULL,'Foster''s Lager is the largest selling Australian beer brand in the world with more than 100 million cases of Foster''s consumed worldwide every year. Brewed in nine countries and over 20 plants, Foster''s Lager is widely sold and distributed throughout Australia, Asia, the Pacific, Europe, the Americas and the Middle East. It is the world''s third most widely distributed brand, available in more than 150 countries. Foster''s Lager is the international flagship brand for the Foster''s group. -http://www.fosters.com.au/enjoy/beer/fosters_the_brand.htm',0,'2010-07-22 20:00:20 UTC');
