INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (634,'Harpoon Brewery - Boston','306 Northern Avenue',NULL,'Boston','Massachusetts','2210','United States','1-888-HARPOON','http://www.harpoonbrewery.com',NULL,NULL,0,'2010-07-22 20:00:20 UTC');
INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (635,'Harpoon Brewery - Windsor','336 Ruth Carney Drive',NULL,'Windsor','Vermont','5089','United States','1-802-674-5491','http://www.harpoonbrewery.com/',NULL,'We started the Harpoon Brewery in 1986 because, like today, we loved beer and wanted more good choices.  While traveling in Europe after college, we drank many wonderfully diverse, fresh, local beers.  We also saw firsthand how important local breweries were to their communities.  After our travels, we asked each other, why not bring great beer and that sense of community to New England?  That was the genesis of the Harpoon Brewery.  Twenty years later we still revel in making great beer and sharing that joy with our friends and neighbors.  The success that we have had running Harpoon is due entirely to the wonderful employees who brew, package, warehouse, sell, deliver, and market our beer and you, the people who drink Harpoon.  We hope that our sense of gratitude is reflected in both the quality of the beer and the spirit of fun and enjoyment surrounding our beer and breweries.',0,'2010-07-22 20:00:20 UTC');
