INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (660,'Hite Brewery',NULL,NULL,'Seoul',NULL,NULL,'Korea, Republic of',NULL,NULL,NULL,'Hite Brewery Company Limited is a South Korean brewery company headquartered in Yeongdeungpo-gu, Seoul. Its main products are beer, rice wine, and mineral water. The company was established as Chosun Breweries in 1933. In 2001 the company had three factories, and in 2002 its share of the domestic beer market was some 55%, up from 30% in 1992.',0,'2010-07-22 20:00:20 UTC');
