INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (669,'Hook & Ladder Brewing Company','8113 Fenton St.',NULL,'Silver Spring','Maryland','20910','United States','301.565.4522','http://www.hookandladderbeer.com',NULL,'At Hook & Ladder Brewing we believe in great beer in the company of good friends, so we bring you three great beers for your drinking pleasure (please drink responsibly). Each of our beers is carefully crafted with the finest quality ingredients for a distinctive taste we know you will enjoy. Try one tonight, you just might get hooked. Through our own experiences in the fire and rescue service we have chosen the Hook & Ladder as a symbol of pride and honor to pay tribute to the brave men and women who serve and protect our communities.',0,'2010-07-22 20:00:20 UTC');
