INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (780,'Legacy Brewing Co.','545 Canal Street',NULL,'Reading','Pennsylvania','19602','United States','(610) 376-9996','http://www.legacybrewing.com',NULL,'Legacy Brewing Co. is committed to handcrafting unique, high quality beer by employing brewing techniques learned from European and American brewers. The complexity of flavors comes from a combination of high quality imported malts and our hopping methods which includes the use of whole flower hops. The Legacy team includes a creative collection of brewers, engineers, a microbiologist and our friends in the arts community; all with a passion for innovative products. Legacy...we brew pure pleasure!',0,'2010-07-22 20:00:20 UTC');
