INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (797,'Live Oak Brewing','3301-B East Fifth Street',NULL,'Austin','Texas','78702','United States','1-512-385-2299','http://www.liveoakbrewing.com/',NULL,'Live Oak Brewing Company is truly a home-grown Austin enterprise. Two of Austin''s own homebrews, Chip McElroy and Brian Peters, literally hand-built the brewery and were the two main brewers, keg washers, delivery persons, and proselytizers. Live Oak has grown since then, but we are still just as passionate about providing delicious old-world style beers that are rarely commercially available. Our special beers are brewed in limited quantities in a small east Austin brewery and are available in fine pubs and restaurants in Austin, Dallas, Houston, Alpine, Marathon, San Marcos and San Antonio.',0,'2010-07-22 20:00:20 UTC');
