INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (801,'Long Trail Brewing Co','Route 4 & 100A',NULL,'Bridgewater Corners','Vermont','5035','United States','802) 672-5011','http://www.longtrail.com/',NULL,'Long Trail Brewery is a microbrewery located in Bridgewater, Vermont, USA. It was opened in May of 1989 by Andy Pherson, who now serves both as the company''s president and brewmaster. The company''s first brewery was located in the basement of the Bridgewater Woolen Mill in Vermont''s Green Mountains, but by 1995 the company had become popular enough that it was able to relocate to a state-of-the-art brewery and visitor center (featuring an open-air deck overlooking the Ottaquechee River) in Bridgewater Corners, VT.',0,'2010-07-22 20:00:20 UTC');
