INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1009,'Port Brewing Company','155 Mata Way Suite 104',NULL,'San Marcos','California','92069','United States','(760) 891-0272','http://www.portbrewing.com/',NULL,'We officially opened on May 5th 2006. Now we will be able to make up to 5000 barrels of beer enabling you more chances to drink your favorite Port Brewed beers at home and around town. More impressive is our oak barrel room where we age our barrel aged specialty beers. Come check out our tasting bar on Fridays and Saturdays where you can get bottles, jugs & merchandise to go while you sample what we''ve been brewing!',0,'2010-07-22 20:00:20 UTC');
