INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1091,'Saint Arnold Brewing','2522 Fairway Park Drive',NULL,'Houston','Texas','77092','United States','1-713-686-9494','http://www.saintarnold.com',NULL,'Saint Arnold Brewing Company, located in Houston, is Texas'' Oldest Craft Brewery. Our goal is to brew world class beers and deliver them to our customers as fresh as possible making them the best beers in Texas. Our customers are beer lovers - people that appreciate great, full-flavored beers.  Our small crew of seventeen does everything at the brewery: brew the beer, filter the beer, keg the beer, bottle the beer, sell the beer and drink the beer. For us, this is a passion, not a job. We believe that this comes through in the beers we make. Our beers have soul.',0,'2010-07-22 20:00:20 UTC');
