INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1157,'Sly Fox Brewhouse and Eatery - Phoenixville','519 Kimberton Road',NULL,'Phoenixville','Pennsylvania','19460','United States','1-610-935-4540','http://www.slyfoxbeer.com/index1.asp',NULL,'A family owned brewpub founded in 1995. A second location, in Royersford, PA, was opened in 2004 and includes a full production brewery as well as an accompanying restaurant. Head brewer is Brian O''Reilly, a multi-GABF Medal winner. Sly Fox packages its beers in cans, 22oz and 750ml bottles, but not in the standard 12oz-er. The pub is noted for its annual Bock Festival & Goat Race (winning goat''s name goes on the year''s MaiBock) and the IPA Project, a year-long program of single varietal beers culminating in an Imperial IPA using all those varietals, released at a day-long party every December when all the IPAs are on draught.',0,'2010-07-22 20:00:20 UTC');
INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1158,'Sly Fox Brewhouse and Eatery - Royersford','312 North Lewis Road',NULL,'Royersford','Pennsylvania','19468','United States','1-610-948-8088','http://www.slyfoxbeer.com/index1.asp',NULL,'A full production brewery and accompanying restaurant opened in 2004 by the owners of Sly Fox Brewhouse & Eatery in nearby Phoenixville. The 20bbl brewhouse packages a wide range of beers in 12oz cans, 22oz bottles and 750ml bottles. Pikeland Pils, a multiple GABF Gold Medal Winner, and O''Reilly''s Stout, a nitro-poured, draught-only beer named after brewer Brian O''Reilly''s grandfather are among its most popular beers, on and off premises, as are such Belgian-style delights as Incubus Tripel, Ichor Quadrupel, Saison Vos & Black Raspberry Reserve. Sly Fox is also noted for its Bock Beers (see listing for the Phoenixville pub for more details) and annual Christmas Ale.',0,'2010-07-22 20:00:20 UTC');
