INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1193,'Standing Stone Brewing Company','101 Oak Street',NULL,'Ashland','Oregon','97520','United States','(541) 482-2448','http://www.standingstonebrewing.com/',NULL,'Ashland''s only full service brewpub is housed in an historic building with a light industrial character. We offer our own freshly brewed handcrafted ales and lagers, including seasonal and other specialty brews.  All Standing Stone Ales are made here at the brewery in ten barrel batches using over 80% organic malts.',0,'2010-07-22 20:00:20 UTC');
