INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1195,'Steamworks','375 Water Street',NULL,'Vancouver','British Columbia','V6B 5C6','Canada','1-604-689-2739','http://www.steamworks.com/gastown_index.htm',NULL,'Steamworks gets its name from the famous Gastown steam line that runs through our premises. The Gastown Brewing Company, which is located onsite and brews Steamworks beers, uses steam to fire its kettles. The instant heat of steam allows for greater brewing finesse, and most importantly great tasting beer. Whether you''re in the mood for a thirst quenching Lions Gate lager, or a full bodied HeriocaTM Oatmeal Stout, we''ve got a beer to please your palate.',0,'2010-07-22 20:00:20 UTC');
