INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1208,'Stonecutters Brewhouse','14 N Main St.',NULL,'Barre','Vermont','5641','United States','(802) 476-6000','http://www.stonecuttersbrewhouse.com/',NULL,'Stonecutters Brewhouse is a 4 barrel brewhouse located in central Vermont in the small town (pop. 9000) of Barre. Barre is the sister town to Montpelier, which is the capital of Vermont. Stonecutters is a locally owned and operated.',0,'2010-07-22 20:00:20 UTC');
