INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1244,'The Alchemist','23 South Main Street',NULL,'Waterbury','Vermont','5676','United States','(802) 244-4120','http://www.alchemistbeer.com/',NULL,'The Alchemist is a 7 barrel brew pub specializing in hand-crafted beer and casual pub fare. All of our ales are brewed in our basement brewery, which was designed and installed by our brewer and co-proprietor John Kimmich. We use only the finest imported malts and domestic hops available to bring you the tastiest and finest selection of beers in Vermont!',0,'2010-07-22 20:00:20 UTC');
