INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1246,'The Bruery','715 Dunn Way',NULL,'Placentia','California','92870','United States','714-996-6258','http://www.thebruery.com/',NULL,'We are A small craft brewery located in Orange County, California. Our simple but often confusing name is a fusion of ''brewery'' with our family name, Rue. Our calling is to create beers with character and depth using the simplest and purest of means. We do not filter or pasteurize our beers. All of our bottled beers gain carbonation through 100% bottle conditioning, allowing carbonation to naturally occur through a secondary bottle fermentation. We strive to use unconventional ingredients, and we will proudly state what we put in our beers. We enjoy serving you, your friends and your family.',0,'2010-07-22 20:00:20 UTC');
INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1247,'The Cambridge House','357 Salmon Brook Street',NULL,'Granby','Connecticut','6035','United States','(860) 653-BREW','http://www.cambridgebrewhouse.com/',NULL,'The best brewery in the Connecticut river valley.  Hands down!  It serves 2 beers that have won a total of 3 gold medals at big beer fests.',0,'2010-07-22 20:00:20 UTC');
