INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1253,'The Round Barn Winery & Brewery','10983 Hills Road',NULL,'Baroda','Michigan','49101','United States','(800)-716-9463','http://www.roundbarnwinery.com/brewery.php',NULL,'With more than 30 years of fermentation experience and an award-winning reputation in the industry, our line of hand-crafted brews embody the satisfying bold taste you''ve come to expect from The Round Barn.',0,'2010-07-22 20:00:20 UTC');
INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1254,'Third Street Ale Works','610 Third Street',NULL,'Santa Rosa','California','95404','United States','1-707-523-3060','http://www.thirdstreetaleworks.com',NULL,NULL,0,'2010-07-22 20:00:20 UTC');
INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1255,'Thirsty Dog Brewing','529 Grant St. Suite B',NULL,'Akron','Ohio','44311','United States','330-252-2739','http://www.thirstydog.com',NULL,'Here at the Thirsty Dog Brewing Company, full flavor, delicate balance, texture & aroma combine to make beer an infinitely complex experience, and one that can be thoroughly enjoyable. Each of our beers are meticulously hand crafted in small batches using the finest ingredients. High quality malted barley is the source of the beer''s sweetness & body. Select American & European hops are used to balance the malt sweetness, and , at times, add special flavors & aromas to the beer. Our careful selection of ingredients, along with specially designed recipes & brewing processes, give Thirsty Dog beers their unique flavor profiles that can be hard to find in today''s mass market. one size fits all society. Our microbrewed beers are far more diverse in range and character then the small spectrum of mass-marketed beers offered to the American public by the large national breweries. Enjoy the diversity, Unleash the flavor today., Welcome to Thirsty Dog Brewing Company, and welcome to great beer!',0,'2010-07-22 20:00:20 UTC');
