INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1307,'Unibroue','80 Des Carrires',NULL,'Chambly','Quebec',NULL,'Canada','1-450-658-7658','http://www.unibroue.com',NULL,'There is more to certain beers than simple refreshment. Unibroue has raised the standards of conventional beer in by brewing craftsman beers for savouring.  Of incomparable quality and a taste, the beers at Unibroue are entirely natural, contain no chemical additives or preservatives, and are made with first quality ingredients.  Rediscover beer with Unibroue. You will never taste beer the same way again!',0,'2010-07-22 20:00:20 UTC');
