INSERT INTO brewery(id,name,address1,address2,city,state,code,country,phone,website,filepath,description,add_user,last_modified) VALUES (1328,'Vintage 50 Restaurant and Brew Lounge','50 Catoctin Ct. NE #100',NULL,'Leesburg','Virginia','20176','United States','(703) 777-2169','http://www.vintage50.com/',NULL,'Vintage 50, at this Leesburg, Virginia classically modern brewery.  Whether you enjoy a fine California cabernet paired with innovative New American fare, or a cold beer brewed on-site paired with classic comfort food, you will feel at home at Vintage 50. Guests will relax in style while enjoying libations from near and far as well as a menu that features fresh, seasonal ingredients.',0,'2010-07-22 20:00:20 UTC');
